import axios from 'axios'
import React, { useEffect, useState } from 'react'
import { Link, Route, Redirect, useParams } from 'react-router-dom'

export default function Edit() {
    const [user, setUser] = useState([])
    const [name, setName] = useState('')
    const [city, setCity] = useState('')
    const params = useParams()
    const histroy = useHistory()
    useEffect(() => {
       console.log('params::',params)
       var data = params.id
        axios.post('http://localhost:1000/users/findUser',{user_id: params.id})
        .then((res) => {
            console.log('Edit response=>',res.data[0].name)
            setName(res.data[0].name)
            setCity(res.data[0].city)

        })
        .catch((err) => { console.log('errour=>', err) })
       
    }, [])

    function handleClick(e) {
        var data = {
            name: name,
            city: city,
            id: params.id
        }
        setUser(data)

        axios.post('http://localhost:1000/users/updateUser', data)
            .then((res) => {
                console.log('res=>',res.data)
                // <Route>
                //     <Redirect to="/users" />
                // </Route>

            })
            .catch((err) => { console.log('errour=>', err) })    
        }


    return (
        <div>
            <h1>Hello from edit</h1>
            <div>
                
                <input type="text" name="name" placeholder="text" value={name} onChange={(e) => { setName(e.target.value) }}></input>
                <input type="text" name="city" placeholder="city" value={city} onChange={(e) => { setCity(e.target.value) }}></input>

                <input type="button" onClick={handleClick} value="Update" />
            </div>
        </div>
    )
}
