    const [user, setUser] = useState([])
    const [name, setName] = useState('')
    const [city, setCity] = useState('')
    // const history = useHistory()

    var method =""
    var data =''
    var url =''


    function handleClick(e){
        var data = {
            name:name,
            city:city
        }
        setUser(data)
        
        axios.post('http://localhost:1000/users/addName',data)
            .then((res)=>{
                // console.log('res=>',res.data)
                history.go(-1)
                
            })
            .catch((err)=>{console.log('errour=>',err)})
    }


    return (
        <div>
            <h1>Hello first MERN project</h1>
            <input type="text" name="name" placeholder="text" value={name} onChange={(e) => { setName(e.target.value) }}></input>
            <input type="text" name="city" placeholder="city" value={city} onChange={(e) => { setCity(e.target.value) }}></input>

            <input type="button" onClick={handleClick} value="Click here" />
        </div>
    )
}
