import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios'

export default function View() {
    const [allUsers, setAllUsers] = useState('')
    const [user, setUser] = useState([])

    useEffect(() => {
        var method = "get"
        var url = "http://localhost:1000/users"
        axios.get(url)
            .then((res) => {
                console.log('res=>', res.data)
                var resp = {
                    name: res.data.name,
                    city: res.data.city
                }
                console.log('resp =>', resp.name, resp.city)
                console.log('actual response =>', res.data)

                setAllUsers(res.data)

            })
            .catch((err) => { console.log('errour=>', err) })
    }, [])

    const users = allUsers.length > 0 ? (allUsers.map((user, key) => {
        return (
            <div key={user._id} style={{ border: "1px solid black", margin: "10px" }}>
                <h1>{user.name},</h1><h4>{user.city} </h4>

         
                <Link to="/editUser/`/editUser/Â´"><button name="edit" value="edit">edit</button></Link>
                <Link to="/delete"><button name="edit" value="edit">Delete</button></Link>


            </div>
        )
    })) : "[]"

    return (
        <div>
            <h1>Users list </h1>
            <h2>{users} </h2>
        </div>
    )
}
