import React from 'react'
import "bootstrap/dist/css/bootstrap.min.css";
import  {useSelector} from 'react-redux'

export default function Comp_todo() {
    const todoSelector = useSelector(store => store)
    console.log(todoSelector.tasks)
    return (
        <div className="todList">

            <h1>Number of tasks in the lists : {todoSelector.tasks.length} </h1>

            <button className="btn btn-success" value="button">button</button>
        </div>
    )
}
