const initialState = {
    tasks : []
}

function todoReducer(state=initialState, action)
{
    switch(action.type)
    {
        case "ADD_TASK": return {
            ...state,
            tasks:[...state.tasks, action.payload]
            
        }
        case "DELETE_TASK": return {
           
            ...state,
            tasks: [ state.tasks.splice(action.payload)]
        }
        // case "REMOVE_TASK":""
        // default: ""

        
    }
    return state
}

export default todoReducer
